import{_ as i}from"./chunks/main.BEHCq0zJ.js";import{_ as s,c as o,o as l,ao as a,j as e}from"./chunks/framework.CwJl7_Pr.js";const m=JSON.parse('{"title":"🎬 What is CreatorBox?","description":"","frontmatter":{},"headers":[],"relativePath":"what-is-creatorbox.md","filePath":"what-is-creatorbox.md","lastUpdated":1747993386000}'),c={name:"what-is-creatorbox.md"};function d(n,t,r,b,p,u){return l(),o("div",null,t[0]||(t[0]=[a('<h1 id="🎬-what-is-creatorbox" tabindex="-1">🎬 What is CreatorBox? <a class="header-anchor" href="#🎬-what-is-creatorbox" aria-label="Permalink to &quot;🎬 What is CreatorBox?&quot;">​</a></h1><p><strong>CreatorBox</strong> is designed for creators, focusing on solving repetitive tasks in content creation workflows such as <code>video translation</code>, <code>cross-language dubbing</code>, and <code>personalized dubbing</code>. Simply provide the materials, and it will automatically complete speech recognition, translation, synthesis, and draft export, enhancing creation efficiency and unleashing creative potential!</p><h2 id="📦-quick-start" tabindex="-1">📦 Quick Start <a class="header-anchor" href="#📦-quick-start" aria-label="Permalink to &quot;📦 Quick Start&quot;">​</a></h2><p>👉 Refer to the usage guide: <a href="/CreatorBox/deploy-local.html">Local Installation</a> | <a href="/CreatorBox/deploy-colab.html">Remote Deployment</a></p><div class="tip custom-block" style="padding-top:8px;"><p><strong>CreatorBox</strong>, designed for creators, enhances creation efficiency and unleashes creative potential.</p></div><p><img src="'+i+'" alt="" width="150" loading="lazy"></p><h2 id="📺-video-demonstration" tabindex="-1">📺 Video Demonstration <a class="header-anchor" href="#📺-video-demonstration" aria-label="Permalink to &quot;📺 Video Demonstration&quot;">​</a></h2>',7),e("p",null,[e("div",{class:"embed-responsive embed-responsive-16by9"},[e("iframe",{class:"embed-responsive-item youtube-player",type:"text/html",width:"640",height:"390",src:"https://www.youtube.com/embed/PERwuqvvFv8?autoplay=0&start=0",frameborder:"0",webkitallowfullscreen:"",mozallowfullscreen:"",allowfullscreen:""})])],-1),a('<h2 id="🎨-what-can-it-do" tabindex="-1">🎨 What Can It Do? <a class="header-anchor" href="#🎨-what-can-it-do" aria-label="Permalink to &quot;🎨 What Can It Do?&quot;">​</a></h2><ul><li><p>🎥 <strong>Content Creators</strong>: Optimize video dubbing, translation, and editing workflows, enhancing creation efficiency and unleashing creative potential.</p></li><li><p>🌍 <strong>Multilingual Translation/Dubbing</strong>: Operate localized content creation and cross-language publishing.</p></li><li><p>⚙️ <strong>Independent Deployment</strong>: Deploy locally with flexible configurations to ensure privacy.</p></li></ul><h2 id="🎯-features" tabindex="-1">🎯 Features <a class="header-anchor" href="#🎯-features" aria-label="Permalink to &quot;🎯 Features&quot;">​</a></h2><ul><li><p><strong>🎤 Speech Recognition</strong></p><p>Accurately transcribe voices from videos and audio, with flexible configuration adjustments to suit different devices and scenarios, ensuring high-quality text generation.</p></li><li><p><strong>🌐 Language Translation</strong></p><p>Translate between multiple languages, allowing switching of translation providers and adjustment of advanced parameters to optimize translation results and easily overcome language barriers.</p></li><li><p><strong>🎧 Speech Synthesis</strong></p><p>A rich library of voices and customization options provide a personalized dubbing experience to meet creative needs, with real-time preview ensuring precise creation.</p></li><li><p><strong>✂️ Draft Export</strong></p><p>Supports exporting materials to editing tools, offering fine-grained control over visuals, audio, and subtitles, aiding post-production adjustments and customization.</p></li><li><p><strong>📤 Scene Modes</strong></p><p>Switch between various creation modes at any time, supporting video dubbing, translation, narration, and other scenarios to meet diverse user needs.</p></li><li><p><strong>🔧 Preview and Debugging</strong></p><p>Provides flexible and efficient configuration adjustments to ensure perfect presentation at every stage, enhancing creation efficiency and quality.</p></li></ul><h2 id="📅-planned-support" tabindex="-1">📅 Planned Support <a class="header-anchor" href="#📅-planned-support" aria-label="Permalink to &quot;📅 Planned Support&quot;">​</a></h2><h3 id="recognition" tabindex="-1">Recognition <a class="header-anchor" href="#recognition" aria-label="Permalink to &quot;Recognition&quot;">​</a></h3><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-0" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-0"> Support for video <code>download</code>, <code>separation</code>, and <code>noise reduction</code>.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-1" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-1"> Support for <code>audio and video</code> transcription and <code>subtitle loading</code>.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-2" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-2"> Support for <code>speaker</code> , <code>voiceprint alignment</code>.</label></li></ul><h3 id="speech" tabindex="-1">Speech <a class="header-anchor" href="#speech" aria-label="Permalink to &quot;Speech&quot;">​</a></h3><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-3" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-3"> Support for <code>multilingual speech</code> synthesis with adjustable speed and preview.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-4" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-4"> Support for <code>voice cloning</code> with three built-in tone types: <code>default</code>, <code>video</code>, and <code>custom</code>, all managed in a unified system.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-5" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-5"> Support for <code>voice commands</code>, including built-in <code>regional accents</code>, <code>speaking styles</code>, and <code>role-playing</code> instructions.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-6" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-6"> Support for <code>voice tone conversion</code>, enabling the transformation of a speaker&#39;s tone in an existing audio file to match the target speaker&#39;s tone.</label></li></ul><h3 id="modes" tabindex="-1">Modes <a class="header-anchor" href="#modes" aria-label="Permalink to &quot;Modes&quot;">​</a></h3><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-7" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-7"> Support for <code>video</code>, <code>audio</code>, and <code>adaptive</code> 3 types of <a href="https://github.com/xiesx123/CreatorBox/discussions/1" target="_blank" rel="noreferrer"><code>dubbing align modes</code></a></label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-8" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-8"> Support for <a href="https://github.com/xiesx123/CreatorBox/discussions/1" target="_blank" rel="noreferrer"><code>translation mode</code></a>: Translate videos from the original language to another language.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-9" disabled="disabled"><label class="task-list-item-label" for="task-item-9"> Support for narration mode (planned).</label></li></ul><h3 id="draft" tabindex="-1">Draft <a class="header-anchor" href="#draft" aria-label="Permalink to &quot;Draft&quot;">​</a></h3><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-10" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-10"><code>Track Control</code>: Support for controlling up to <code>6</code> tracks, including <code>visuals</code>, <code>audio</code>, and <code>subtitles</code>.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-11" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-11"><code>Subtitle Generation</code>: Support for font settings such as <code>size</code>, <code>position</code>, <code>color</code>, and <code>outline</code>.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-12" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-12"><code>Volume Adjustment</code>: Support for adjusting the volume of <code>original sound</code>, <code>speech</code>, and <code>background music</code>.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-13" disabled="disabled"><label class="task-list-item-label" for="task-item-13"><code>Background Music</code>: Random or specified music files.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-14" disabled="disabled"><label class="task-list-item-label" for="task-item-14"><code>Transition Control</code>: For use with long videos.</label></li></ul><h3 id="debugging" tabindex="-1">Debugging <a class="header-anchor" href="#debugging" aria-label="Permalink to &quot;Debugging&quot;">​</a></h3><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-15" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-15"> Configuration adjustments for <code>subtitles</code>, <code>speakers</code>, <code>translation</code>, and <code>dubbing</code>.</label></li></ul><h3 id="others" tabindex="-1">Others <a class="header-anchor" href="#others" aria-label="Permalink to &quot;Others&quot;">​</a></h3><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-16" disabled="disabled"><label class="task-list-item-label" for="task-item-16"> Support for long videos.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-17" disabled="disabled"><label class="task-list-item-label" for="task-item-17"> Automated publishing and submission.</label></li></ul>',17)]))}const g=s(c,[["render",d]]);export{m as __pageData,g as default};
